mapper.select:
SELECT nnum, ntitle, ncontent, userId, npasswd, nview, ndate
FROM notice
WHERE nnum = #{nnum}

SELECT nnum, ntitle
FROM notice
WHERE nnum > #{nnum}
ORDER BY
nnum
ASC LIMIT 1;

mapper.update:
UPDATE notice
SET nview = nview + 1
WHERE nnum = #{nnum}

mapper.select:
SELECT
COUNT(nnum) as cnt
FROM notice
WHERE nnum=#{nnum}
AND npasswd=#{npasswd}

mapper.update:
UPDATE notice

	ntitle = #{ntitle} , ncontent = #{ncontent},

WHERE nnum=#{nnum}
