mapper.update:
update image
set viewCount = viewCount +1
where imgId = #{imgId};

update image
set downloadCount = downloadCount +1
where imgId = #{imgId}

mapper.insert:
insert into image(
    title , tag, imgDescription, imgUrl,imgType, registerDate)
values(#{title},#{tag},#{imgDescription},#{imgUrl},#{imgType},sysdate() );
